interface Item {
  id: string;
  prodId: string;
  imageUrl: string;
  name: string;
  price: number;
  originalPrice: number;
  quantity: number;
}

const Cart: React.FC = () => {
  const {
    data: userCartData,
    loading,
    error,
    refetch,
  } = useQuery<UserCartsResponse>(GET_CART_LIST , {
    variables:{
      shopId:SHOP_ID,
    }
  });
  const [items, setItems] = useState<Item[]>([]);
  const [mutationUpdateCart] = useMutation(UPDATE_CART);
  const [mutationMutipleDeleteCard] = useMutation(MUTIPLE_DELETE_CART);

  // S dng useRef  lu tr timeout cho debounce
  const debounceTimeout = useRef<NodeJS.Timeout | null>(null);

  useEffect(() => {
    if (userCartData) {
      const formattedItems =
        userCartData?.userCarts?.data?.map((item) => ({
          id: item._id,
          prodId: item.prodId,
          imageUrl: item.product.image[0]?.url
            ? RemoveLink(item.product.image[0].url)
            : "/assets/img/product-default.jpg",
          name: item.product.name,
          price: item.product.price,
          originalPrice: item.product.price,
          quantity: item.quantity,
        })) ?? [];
      setItems(formattedItems);
    }
  }, [userCartData]);

  // Hm xa sn phm
  const removeItem = async (id: string) => {
    const updatedItems = items.filter((item) => item.id !== id);
    setItems(updatedItems);

    try {
      const response = await mutationMutipleDeleteCard({
        variables: {
          cartIds: [id],
        },
      });
      if (response.data?.multipleDeleteCart?.success) {
        const newData = await fetchUserCart();
        userCartVar(newData);
        message.success(" xa sn phm thnh cng");
      }
      if (!response.data?.multipleDeleteCart?.success) {
        console.error("Failed to remove item from the backend");
        setItems(items); // Khi phc nu xa tht bi
      }
    } catch (error) {
      console.error("Error while removing item:", error);
      setItems(items); // Khi phc nu c li
    }
    refetch();
  };

  // Hm debounce  cp nht s lng sn phm
  const updateQuantity = (prodId: string, newQuantity: number) => {
    // Hy b timeout trc  nu ngi dng tip tc click
    if (debounceTimeout.current) {
      clearTimeout(debounceTimeout.current);
    }

    // Cp nht UI ngay lp tc
    setItems(
      items.map((item) =>
        item.prodId === prodId ? { ...item, quantity: newQuantity } : item
      )
    );

    // t li timeout, i 0.5 giy sau khi ngi dng ngng click  gi API
    debounceTimeout.current = setTimeout(async () => {
      try {
        const { data } = await mutationUpdateCart({
          variables: { prodId, quantity: newQuantity },
        });

        if (data?.updateCart?.success) {
          message.success("Cp nht s lng thnh cng");
        }
        if (!data?.updateCart?.success) {
          console.error("Failed to update item quantity in the backend");
        }
      } catch (error) {
        console.error("Error while updating item quantity:", error);
      }
    }, 500); // 0.5 giy
    refetch();
  };

  const totalItems = userCartData?.userCarts.links?.totalItems || 0;
  const totalPrice = items.reduce(
    (sum, item) => sum + item.price * item.quantity,
    0
  );

  if (loading) return <Loading />;
  if (error) return <p>Error loading cart data.</p>;

  return (
    <>
      {totalItems !== 0 && (
        <div className="bg-teriaty">
          <div className="inner-container">
            <div className="grid lg:grid-cols-12 grid-cols-4 grid-rows-1 gap-12 py-6 pb-[108px] lg:px-0 px-4">
              <div className="lg:col-span-8 col-span-4">
                <div
                  className="flex flex-col lg:p-[36px_48px] p-[24px_16px] items-start gap-[24px] flex-[1_0_0] bg-white"
                  style={{
                    boxShadow: "0px 4px 20px 0px rgba(194, 194, 194, 0.50)",
                  }}
                >
                  <SectionHeader title="Gi hng" suggestItem={true} />
                  <div className="flex flex-col justify-center items-start gap-4 self-stretch">
                    <h3 className="text-normal uppercase text-start">
                      Sn phm
                    </h3>
                    <span id="countCardItem" className="text-normal text-start">
                      C {totalItems} sn phm trong gi hng
                    </span>

                    {items.map((item) => (
                      <CartItem
                        key={item.id}
                        imageUrl={item.imageUrl}
                        title={item.name}
                        price={item.price}
                        originalPrice={item.originalPrice}
                        quantity={item.quantity}
                        onQuantityChange={(newQuantity) =>
                          updateQuantity(item.prodId, newQuantity)
                        }
                        onRemove={() => removeItem(item.id)}
                      />
                    ))}
                  </div>
                </div>
              </div>
              <div className="lg:col-span-4 lg:col-start-9 col-span-4 col-start-1">
                <div
                  className="flex w-full lg:p-[36px_48px] p-[24px_16px] flex-col items-center gap-6 flex-shrink-0 self-stretch bg-teriaty"
                  style={{
                    boxShadow: "0px 4px 20px 0px rgba(194, 194, 194, 0.50)",
                  }}
                >
                  <div className="flex justify-between items-center self-stretch text-normal">
                    <span>Tm tnh: </span>
                    <div id="totalPrice" className="text-primary-default">
                      {totalPrice.toLocaleString("vi-VN")}{" "}
                      <span className="underline"></span>
                    </div>
                  </div>
                  <Link
                    href={Routers.order.pathOrder}
                    className="btn bg-primary-default text-white uppercase"
                  >
                    Tin hnh t hng
                  </Link>
                  <Link
                    href={Routers.product.pathHotProduct}
                    className="btn text-primary-default uppercase"
                  >
                    {leftIcon}
                    TIP TC MUA HNG
                  </Link>
                </div>
              </div>
            </div>
          </div>
         
        </div>
      )}{" "}
      {totalItems === 0 && (
        <div className="bg-teriaty">
          <div className="inner-container flex justify-center">
            <div className="w-[700px] block">
              <div className="flex w-full flex-col py-9 justify-center items-center gap-6 pb-[190px] lg:px-0 px-6">
                <div>{cartIcon}</div>
                <p>Gi hng ca bn ang trng</p>
                <p>Hy chn thm sn phm  mua sm nh !</p>
                <div className="flex w-full justify-center items-center gap-6">
                  <Link
                    href={Routers.product.pathHotProduct}
                    className="btn-lg border bg-primary-default text-white uppercase"
                  >
                    Mua Sm Ngay
                  </Link>
                </div>
              </div>
            </div>
          </div>
         
        </div>
      )}
    </>
  );
};

export default Cart;
